import React, { Component } from "react";
import { PokemonConsumer } from './PokemonContext';
import MyPokemonCard from "./MyPokemonCard";

class MyPokemon extends Component {

    state = {
        url: 'https://pokeapi.co/api/v2/${name}',
        pokemon: null
    };

    render() {
        return (
            <PokemonConsumer>
                {({ pokemon, releasepokemon }) => (
                    <React.Fragment>
                        <center>
                            <h2>My Pokemon</h2>
                        </center>
                        {this.state.pokemon ? (
                            <div className="row">
                                {this.state.pokemon.map(pokemon => (
                                    <MyPokemonCard
                                        key={pokemon.name}
                                        name={pokemon.name}
                                        url={pokemon.url}
                                    />
                                ))}
                            </div>
                        ) : (
                                <h1>Loading</h1>
                            )}
                        {
                            pokemon.length > 0 ? pokemon.map(item => {
                                const { name, nickname, id } = item;

                                return (
                                    <div className="myPokemon" key={Math.random()}>
                                        <MyPokemonCard></MyPokemonCard>
                                        <center>
                                            <h3>{nickname}</h3>
                                            <h2>{name}</h2>
                                            <button className="button" onClick={() => releasepokemon(id)}>Release</button>
                                        </center>
                                    </div>
                                );
                            }) : null
                        }

                    </React.Fragment>
                )}
            </PokemonConsumer>
        );
    }
}

export default MyPokemon;
