import React, { Component } from 'react';
import axios from 'axios';

export default class PokemonDetail extends Component {
    state = {
        name: '',
        pokemonIndex: '',
        imageUrl: '',
        imageBackUrl: '',
        types: [],
        desc: '',
        stats: {
            hp: '',
            attack: '',
            defense: '',
            speed: '',
            specialAttack: '',
            specialDefense: ''
          },


    }

    async componentDidMount() {
        const { pokemonIndex } = this.props.match.params; //grab the pokemon index from the params

        const pokeurl = `https://pokeapi.co/api/v2/pokemon/${pokemonIndex}/`;
        const pokeSpeciesUrl = `https://pokeapi.co/api/v2/pokemon-species/${pokemonIndex}/`;

        //get pokemon data and info
        const pokemonRes = await axios.get(pokeurl);
        const name = pokemonRes.data.name;
        const imageUrl = pokemonRes.data.sprites.front_default;
        const imageBackUrl = pokemonRes.data.sprites.back_default;

        let { hp, attack, defense, speed, specialAttack, specialDefense } = '';
    }
    render() {
        return (
            <div>
                <h1>{this.state.name}</h1>
            </div>
        )
    }
}
