import React, { Component } from "react";
import { PokemonConsumer } from './PokemonContext';
import MyPokemonCard from './MyPokemonCard';

class MyPokemon extends Component {

    state = {
        url: 'https://pokeapi.co/api/v2/${name}',
        pokemon: null
    };

    render() {
        return (
            <PokemonConsumer>
                {({ pokemon, releasepokemon }) => (
                    <React.Fragment>
                        <center>
                            <h2>My Pokemon</h2>
                        </center>
                        {this.state.pokemon ? (
                    <div className="row">
                        {this.state.pokemon.map(pokemon => (
                            <MyPokemonCard
                                key={pokemon.name}
                                name={pokemon.name}
                                url={pokemon.url}
                            />
                        ))}
                    </div>
                ) : (
                        <h1>Loading</h1>
                    )}
                    </React.Fragment>
                )}
            </PokemonConsumer>
        );
    }
}

export default MyPokemon;
