import React, { Component } from 'react';
import axios from 'axios';
import Button from 'react-bootstrap/Button';
import { PokemonConsumer } from './PokemonContext.js';

const TYPE_COLORS = {
  bug: 'B1C12E',
  dark: '4F3A2D',
  dragon: '755EDF',
  electric: 'FCBC17',
  fairy: 'F4B1F4',
  fighting: '823551D',
  fire: 'E73B0C',
  flying: 'A3B3F7',
  ghost: '6060B2',
  grass: '74C236',
  ground: 'D3B357',
  ice: 'A3E7FD',
  normal: 'C8C4BC',
  poison: '934594',
  psychic: 'ED4882',
  rock: 'B9A156',
  steel: 'B5B5C3',
  water: '3295F6'
};

export default class PokemonDetail extends Component {
  state = {
    name: '',
    pokemonIndex: '',
    imageUrl: '',
    imageBackUrl: '',
    types: [],
    desc: '',
    stats: {
      hp: '',
      attack: '',
      defense: '',
      speed: '',
      specialAttack: '',
      specialDefense: ''
    },
    height: '',
    weight: '',
    eggGroups: '',
    catchRate: '',
    abilities: '',
    genderRatioMale: '',
    genderRatioFemale: '',
    evs: '',
    hatchSteps: '',
    moves: [],
  }

  async componentDidMount() {
    const { pokemonIndex } = this.props.match.params;

    // Urls for pokemon information
    const pokemonUrl = `https://pokeapi.co/api/v2/pokemon/${pokemonIndex}/`;
    const pokemonSpeciesUrl = `https://pokeapi.co/api/v2/pokemon-species/${pokemonIndex}/`;

    // Get Pokemon Information
    const pokemonRes = await axios.get(pokemonUrl);

    const name = pokemonRes.data.name;
    const imageUrl = pokemonRes.data.sprites.front_default;
    const imageBackUrl = pokemonRes.data.sprites.back_default;

    let { hp, attack, defense, speed, specialAttack, specialDefense } = '';

    pokemonRes.data.stats.map(stat => {
      switch (stat.stat.name) {
        case 'hp':
          hp = stat['base_stat'];
          break;
        case 'attack':
          attack = stat['base_stat'];
          break;
        case 'defense':
          defense = stat['base_stat'];
          break;
        case 'speed':
          speed = stat['base_stat'];
          break;
        case 'special-attack':
          specialAttack = stat['base_stat'];
          break;
        case 'special-defense':
          specialDefense = stat['base_stat'];
          break;
        default:
          break;
      }
    });

    const height = pokemonRes.data.height * 10

    const weight = pokemonRes.data.weight / 10

    const types = pokemonRes.data.types.map(type => type.type.name);

    const moves = pokemonRes.data.moves.map(move => move.move.name);

    const abilities = pokemonRes.data.abilities
      .map(ability => {
        return ability.ability.name
          .toLowerCase()
          .split('-')
          .map(s => s.charAt(0).toUpperCase() + s.substring(1))
          .join(' ');
      })
      .join(', ');

    const evs = pokemonRes.data.stats
      .filter(stat => {
        if (stat.effort > 0) {
          return true;
        }
        return false;
      })
      .map(stat => {
        return `${stat.effort} ${stat.stat.name
          .toLowerCase()
          .split('-')
          .map(s => s.charAt(0).toUpperCase() + s.substring(1))
          .join(' ')}`;
      })
      .join(', ');

    // Get Pokemon Description .... Is from a different end point uggh
    await axios.get(pokemonSpeciesUrl).then(res => {
      let desc = '';
      res.data.flavor_text_entries.some(flavor => {
        if (flavor.language.name === 'en') {
          desc = flavor.flavor_text;
          return;
        }
      });
      const femaleRate = res.data['gender_rate'];
      const genderRatioFemale = 12.5 * femaleRate;
      const genderRatioMale = 12.5 * (8 - femaleRate);

      const catchRate = Math.round((100 / 255) * res.data['capture_rate']);

      const eggGroups = res.data['egg_groups']
        .map(group => {
          return group.name
            .toLowerCase()
            .split(' ')
            .map(s => s.charAt(0).toUpperCase() + s.substring(1))
            .join(' ');
        })
        .join(', ');

      const hatchSteps = 255 * (res.data['hatch_counter'] + 1);

      this.setState({
        desc,
        genderRatioFemale,
        genderRatioMale,
        catchRate,
        eggGroups,
        hatchSteps
      });
    });

    this.setState({
      imageUrl,
      imageBackUrl,
      pokemonIndex,
      name,
      types,
      moves,
      stats: {
        hp,
        attack,
        defense,
        speed,
        specialAttack,
        specialDefense
      },
      height,
      weight,
      abilities,
      evs
    });
  }





  render() {
    const { name, pokemon_details } = this.state;

    function catchPokemon() {
      if (Math.random() >= 0.5) {
        return prompt(name + " berhasil ditangkap \n Masukan nickname untuk pokemon ini");
      } else {
        alert(name + " Gagal ditangkap [Berhasil melarikan diri], Coba lagi :p");
        return false;
      }
    }

    return (
      <PokemonConsumer>
      {({ updatepokemon }) => (
	      <div id="pokemonDetail">
	        <center>
	          <button className="button" onClick={event => {
	          	let nickname = catchPokemon();
	          	if(nickname){
		          	updatepokemon([{name: name, nickname: nickname}]);
		          	alert("Pokemon berhasil ditambahkan ke [My Pokemon]");
	          	}
	          }}>Catch {name}</button>
	          <h1 className="capitalize">{name}</h1>
	          <div>
	            <img 
	              src={name+".jpg"} 
	              title={name} 
	              alt={name}
	            />
	          </div>
	        </center>
	          <div className="row">
	            <div className="row-30">
	              <h2>Types</h2>
	              <ul>
	                {
	                  pokemon_details.types ? 
	                  pokemon_details.types.map(item => {
	                    const {type} = item;
	                     return (
	                        <li key={type.name}>{type.name}</li>
	                    );
	                  }) : null
	                }
	              </ul>
	            </div>
	            <div className="row-30">
	              <h2>Held Items</h2>
	              <ul>
	                {
	                  pokemon_details.held_items ? 
	                  pokemon_details.held_items.map(item_hi => {
	                    const {item} = item_hi;
	                     return (
	                        <li key={item.name}>{item.name}</li>
	                    );
	                  }) : null
	                }
	              </ul>
	            </div>
	            <div className="row-30">
	              <h2>Stats</h2>
	              <ul>
	                {
	                  pokemon_details.stats ? 
	                  pokemon_details.stats.map(item => {
	                    const {stat} = item;
	                     return (
	                        <li key={stat.name}>{stat.name}</li>
	                    );
	                  }) : null
	                }
	              </ul>
	            </div>
	            <div className="row-30">
	              <h2>Moves</h2>
	              <ul>
	                {
	                  pokemon_details.moves ? 
	                  pokemon_details.moves.map(item => {
	                    const {move} = item;
	                     return (
	                        <li key={move.name}>{move.name}</li>
	                    );
	                  }) : null
	                }
	              </ul>
	            </div>
	          </div>
	      </div>
      )}	      
	  </PokemonConsumer>
    )
  }
}
