            }));
    }



    render() {
        return (
            <div>
                <center>
                    <h2>Pokemon List</h2>
                    <h5 className="pb-4">Total Pokemon Owned: </h5>
                </center>
                {this.state.pokemon ? (
                    <div className="row">
                        {this.state.pokemon.map(pokemon => (
                            <PokemonCard
                                key={pokemon.name}
                                name={pokemon.name}
                                url={pokemon.url}
                            />
                        ))}
                    </div>
                ) : (
                        <h1>Loading</h1>
                    )}
            </div>
        )
    }
}
