import React, { Component } from "react";
import { PokemonConsumer } from './PokemonContext';
import PokemonCard from "./PokemonCard";

class MyPokemon extends Component {

    state = {
        url: 'https://pokeapi.co/api/v2/${name}',
        pokemon: null
    };

  render() {
    return (
	 <PokemonConsumer>
      {({ pokemon, releasepokemon }) => (
	 	 <React.Fragment>
	        <h2>My Pokemon</h2>
	        <p>Pokemon-pokemon yang berhasil ditangkap</p>
	        {
              pokemon.length > 0 ? pokemon.map(item => {
              const {name, nickname, id} = item;

               return (
               	<div className="myPokemon" key={Math.random()}>
	                <center>
		                <h3>{nickname}</h3>
						<h2>{name}</h2>
		                <button className="button" onClick={() => releasepokemon(id)}>Release</button>
		            </center>
	            </div>
              );
            }) : null
          }
	     </React.Fragment>
	   )}
     </PokemonConsumer>
    );
  }
}

export default MyPokemon;
